def get_collocations(nlp, data_collocations):
    doc = nlp(data_collocations)
    get_noun_chunks(nlp, data_collocations)
    collocations = []
    for token in doc:
        if token.pos_ == 'ADJ' and token.dep_ == 'amod' and token.head.pos_ == 'NOUN':
            collocations.append((token.text, token.head.text))
        elif token.pos_ == 'NOUN' and token.n_lefts >= 2:
            adjs = [child for child in token.lefts if child.pos_ == 'ADJ']
            if len(adjs) == 2:
                collocations.append((adjs[0].text, adjs[1].text, token.text))
        elif token.pos_ == 'NOUN' and token.head.pos_ == 'ADJ' and token.head.head.pos_ == 'NOUN':
            collocations.append((token.head.head.text, token.head.text, token.text))

    return Counter(collocations).most_common()
